export const getHardcodedLineup = (teamName: string) => {
  if (!teamName) return null;
  
  const cleanName = (name: string) => {
      return name.toLowerCase()
        .replace(/ fc/g, '')
        .replace(/ afc/g, '')
        .replace(/ cf/g, '')
        .trim();
  };

  const cleaned = cleanName(teamName);
  
  for (const key of Object.keys(REAL_LINEUPS)) {
     if (cleanName(key) === cleaned) return REAL_LINEUPS[key];
  }
  
  const aliases: Record<string, string> = {
    "man city": "Manchester City",
    "man utd": "Manchester United",
    "spurs": "Tottenham Hotspur",
    "real madrid": "Real Madrid",
    "barca": "FC Barcelona",
    "psg": "Paris Saint-Germain",
    "bayern": "Bayern Munich",
    "inter": "Inter Milan"
  };
  
  for (const [alias, target] of Object.entries(aliases)) {
    if (cleaned.includes(cleanName(alias))) return REAL_LINEUPS[target];
  }

  return null;
};

export const getPlayerImage = (playerName: string) => {
  if (!playerName) return null;
  const lowerName = playerName.toLowerCase().trim();
  
  for (const team of Object.values(REAL_LINEUPS)) {
    const player = team.find(p => p.name.toLowerCase() === lowerName || p.name.toLowerCase().includes(lowerName));
    if (player && player.image) return player.image;
  }
  return null;
};
